import React, { useState } from 'react';
// import Input from '../Input/Input';
import Radio from '../radiobutton/Radio';
import './Register.css';
import Button from '../Button/Button';

const Form = () => {
  const [progress, setProgress] = useState('Male');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rePassword, setRePassword] = useState('');

  const handleProgressChange = (value) => {
    if (progress !== value) {
      setProgress(value);
    }
  };

  const submit = (e) => {
    e.preventDefault();
    console.log(firstName, lastName, email, password, rePassword, progress);
  };

  return (
    <div className='registerWork'>
      <h1>User Registration</h1>
      <input
        placeholder="First Name"
        inputId="first-name"
        type="text"
        value={firstName}
        onChange={(e) => setFirstName(e.target.value)}
      />
      <input
        placeholder="Last Name"
        inputId="last-name"
        type="text"
        value={lastName}
        onChange={(e) => setLastName(e.target.value)}
      />
      <input
        placeholder="Email"
        inputId="email"
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        placeholder="Password"
        inputId="password"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <input
        placeholder="Re-enter Password"
        inputId="re-enter-password"
        type="password"
        value={rePassword}
        onChange={(e) => setRePassword(e.target.value)}
      />
      <div className='radioButton'>
        <Radio
          name="Gender"
          value="Male"
          label="Male"
          checked={progress === 'Male'}
          onChange={() => handleProgressChange('Male')}
        />
        <Radio
          name="Gender"
          value="Female"
          label="Female"
          checked={progress === 'Female'}
          onChange={() => handleProgressChange('Female')}
        />
        <Radio
          name="Gender"
          value="Others"
          label="Others"
          checked={progress === 'Others'}
          onChange={() => handleProgressChange('Others')}
        />
      </div>
      <Button name="Save" className="buttonWork" onClick={(e) => submit(e)} />
    </div>
  );
};

export default Form;
