import React, { useState } from 'react';
import Input from '../Input/Input';
import Radio from '../radiobutton/Radio';
import './Register.css';
import Button from '../Button/Button';

const Form = () => {
  const [gender, setGender] = useState('Male');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rePassword, setRePassword] = useState('');

  const handleGenderChange = (value) => {
    if (gender !== value) {
      setGender(value);
    }
  };

  const submit = (e) => {
    e.preventDefault();
    // Add form validation here
    if(firstName && lastName && email && password && rePassword && password === rePassword) {
      // Send data to server or use it
    } else {
      // Handle validation errors
    }
  };

  return (
    <div className='registerWork'>
      <h1>User Registration</h1>
      <Input 
        placeholder="First Name" 
        inputId="first-name" 
        type="text" 
        value={firstName} 
        onChange={(e) => setFirstName(e.target.value)} 
      />
      <Input 
        placeholder="Last Name" 
        inputId="last-name" 
        type="text"  
        value={lastName} 
        onChange={(e) => setLastName(e.target.value)} 
      />
      <Input 
        placeholder="Email" 
        inputId="email" 
        type="email" 
        value={email} 
        onChange={(e) => setEmail(e.target.value)} 
      />
      <Input 
        placeholder="Password" 
        inputId="password" 
        type="password" 
        value={password} 
        onChange={(e) => setPassword(e.target.value)} 
      />
      <Input 
        placeholder="Re-enter Password" 
        inputId="re-enter-password" 
        type="password" 
        value={rePassword} 
        onChange={(e) => setRePassword(e.target.value)} 
      />
      <div className='radioButton'>
        <Radio
          name="Gender"
          value="Male"
          label="Male"
          checked={gender === 'Male'}
          onChange={() => handleGenderChange('Male')}
        />
        <Radio
          name="Gender"
          value="Female"
          label="Female"
          checked={gender === 'Female'}
          onChange={() => handleGenderChange('Female')}
        />
        <Radio
          name="Gender"
          value="Others"
          label="Others"
          checked={gender === 'Others'}
          onChange={() => handleGenderChange('Others')}
        />
      </div>
      <Button name="Save" className="buttonWork" onClick={submit} />
    </div>
  );
};

export default Form;
