import React from 'react';
import './Button.css';

const Button = (props) => {
  const handleClick = () => {
    if (props.onClick) {
      props.onClick();
    }
  };

  const Icon = props.icon; // Store the icon component in a variable

  // Define a custom class for icons
  const iconClassName = props.iconClassName || ""; // Default to empty string if not provided

  return (
    <div>
      <button className={props.className} onClick={handleClick}>
        {props.icon && <Icon className={iconClassName} />} {/* Apply the custom class directly to the icon component */}
      </button>
    </div>
  );
};

export default Button;
