import React, { useState } from 'react';
import Input from '../Input/Input';
import './Login.css';
import Button from '../Button/Button';
import { Link } from 'react-router-dom';
import profileImage from '../../assets/login.jpg';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add logic to handle form submission
    console.log('Form submitted');
  };

  return (
    <div className="container-fluid">
      <div className="row justify-content-center">
        <div className="col-4 image-container">
          <img src={profileImage} alt="profile" className='image-fluid'/>
        </div>
        <div className="col-4">
          <div className='formWork'>
            <div className="column">
              <img src={profileImage} alt="Profile" className="profile-image" />
            </div>
            <div className="column">
              <h1 className='h1-work'>User Login</h1>
              <form onSubmit={handleSubmit}>
                <Input 
                  placeholder="Email" 
                  inputId="email" 
                  type="email" 
                  value={email} 
                  onChange={handleEmailChange} 
                />
                <Input 
                  placeholder="Password" 
                  inputId="password" 
                  type="password" 
                  value={password} 
                  onChange={handlePasswordChange} 
                />
                <Button type="submit" name="Save" className="buttonWork" />
              </form>
              <Link to="/register" style={{ paddingTop: '20px' }}>Create new account</Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
