import React, { useState } from 'react';
import Input from '../../components/Input/Input';
import Textarea from '../../components/textarea/Textarea';
import Radio from '../../components/radiobutton/Radio';

const Form = () => {
  const [progress, setProgress] = useState('finished'); // State for radio button value

  const handleProgressChange = (value) => {
    if (progress !== value) {
      setProgress(value);
    }
  };

  return (
    <div>
      <Input placeholder="Todo list Tile" inputId="todoInput" type="text" />
      <Input placeholder="Created By" inputId="createdByInput" type="text" />
      <Input placeholder="Deadline" inputId="deadlineInput" type="date" />
      <Radio
        name="progress"
        value="finished"
        label="Finished"
        checked={progress === 'finished'}
        onChange={() => handleProgressChange('finished')}
      />
      <Radio
        name="progress"
        value="ongoing"
        label="On Going"
        checked={progress === 'ongoing'}
        onChange={() => handleProgressChange('ongoing')}
      />
      <Radio
        name="progress"
        value="goodToGo"
        label="Good to Go"
        checked={progress === 'goodToGo'}
        onChange={() => handleProgressChange('goodToGo')}
      />
      <Textarea placeholder="Description" id="textarea" rows="4" cols="50" />
    </div>
  );
};

export default Form;
